mapper.insert:
insert into customer (
	custid
	, password
	, name
	, email
	, division
	, idno
	, address
)
values (
	#{custid}
	, #{password}
	, #{name}
	, #{email}
	, #{division}
	, #{idno}
	, #{address}
)

mapper.update:
update customer
set
	password = #{password}
	, name = #{name}
	, email = #{email}
	, division = #{division}
	, idno = #{idno}
	, address = #{address}
where
	custid = #{custid}
